mapper.select:
SELECT
v_num, v_name, v_phone, to_char(v_date,'YYYY-MM-DD') as
v_date,
v_time, v_status
from (select list.*, rownum as rnum
from(

) list
)



			rnum between #{start_row} and #{end_row}



group by v_num, v_name, v_phone, v_date, v_time, v_status

order by v_date asc, v_time asc
order by v_date desc, v_time desc

select nvl(count(1), 0) from(
select list.*, rownum as rnum
from(

order by v_num desc
) list
)
